// src/components/SentimentAnalyzer.js

import React, { useState, useRef } from 'react';

const SentimentAnalyzer = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [dragActive, setDragActive] = useState(false);
    const inputRef = useRef(null);

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file && file.type === 'text/csv') {
            setSelectedFile(file);
            console.log('Arquivo CSV selecionado:', file);
        } else {
            setSelectedFile(null);
            alert('Por favor, selecione um arquivo CSV.');
    }
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    event.stopPropagation();
    setDragActive(true);
  };

  const handleDragLeave = (event) => {
    event.preventDefault();
    event.stopPropagation();
    setDragActive(false);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    event.stopPropagation();
    setDragActive(false);

    if (event.dataTransfer.files && event.dataTransfer.files[0]) {
      const file = event.dataTransfer.files[0];
      if (file.type === 'text/csv') {
        setSelectedFile(file);
        console.log('Arquivo CSV arrastado:', file);
      } else {
        setSelectedFile(null);
        alert('Por favor, arraste apenas um arquivo CSV.');
      }
    }
  };

  const handleButtonClick = () => {
    inputRef.current.click();
  };

  //Refatorar aqui
  const handleContinueClick = () => {
    if (selectedFile) {
        //console.log(selectedFile);
    } else {
      console.log('Nenhum arquivo selecionado.');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      <div className="max-w-lg w-full bg-white shadow-md rounded-lg p-6">
        <h1 className="text-3xl font-bold text-center mb-4">Analisador de sentimentos</h1>
        <p className="text-center text-gray-600 mb-6">
          um breve texto explicando sobre o projeto e como funciona
        </p>
        <div
          className={`border-2 ${dragActive ? 'border-blue-500' : 'border-gray-400'} border-dashed rounded-lg p-8 flex flex-col items-center justify-center`}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
        >
          <input
            type="file"
            ref={inputRef}
            accept=".csv"
            onChange={handleFileChange}
            className="hidden"
          />
          <button
            onClick={handleButtonClick}
            className="bg-gray-800 text-white font-bold py-2 px-4 rounded mb-4 hover:bg-gray-700"
          >
            Fa√ßa Upload
          </button>
          <p className="text-gray-600">ou arraste um arquivo CSV</p>
        </div>
        {selectedFile && (
          <div className="mt-4 text-center">
            <p className="text-gray-800 font-medium">Arquivo selecionado: {selectedFile.name}</p>
          </div>
        )}
        <div className="flex justify-center mt-6">
          <button
            onClick={handleContinueClick}
            disabled={!selectedFile}
            className={`py-2 px-6 font-bold rounded ${
              selectedFile
                ? 'bg-blue-600 text-white hover:bg-blue-500'
                : 'bg-gray-400 text-gray-700 cursor-not-allowed'
            }`}
          >
            Continuar
          </button>
        </div>
      </div>
    </div>
  );
};

export default SentimentAnalyzer;
