import React from 'react';
import GraphArea from '../../components/Grafico';
import FilterButtons from '../../components/Filtro';
import SearchBar from '../../components/BarraDeBusca';
import WordListTable from '../../components/Tabela';

const App = () => {
  return (
    <div className="h-screen w-screen flex gap-5">
      {/* Área do Grafo */}
      <div className="flex-grow bg-gray-100 border-2 border-purple-500 h-96 md:w-1/2 flex items-center justify-center">
        <span className="text-gray-500 transform rotate-[-45deg] text-3xl">Grafo Aqui</span>
      </div>

      {/* Área de Controle */}
      <div className="flex flex-col md:w-1/3 space-y-4">
        {/* Barra de Adição */}
        <div className="flex space-x-2">
          <input
            type="text"
            className="border p-2 flex-grow rounded-md"
            placeholder="Digite aqui"
          />
          <button className="bg-gray-300 p-2 rounded-md">
            <span>+</span>
          </button>
        </div>

        {/* Botões de Filtro */}
        <FilterButtons />

        {/* Barra de Busca */}
        <SearchBar />

        {/* Tabela de Palavras */}
        <WordListTable />
      </div>
    </div>
  );
};

export default App;
