import React from "react";
import Graph from "../../components/Grafico";
import Filters from "../../components/Filtro";

const FeedbackComponent = () => {
  return (
    <div className="flex flex-col lg:flex-row p-8 gap-8 bg-backgroundcolor min-h-screen">
      <Graph/>

      {/* Seção de Feedback */}
      <div className="flex-1 lg:max-w-lg bg-white shadow-md rounded-lg p-6">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-bold text-textcolor">Feedback Geral do Produto: </h2>
          <span className="text-sm font-bold text-red-600 bg-red-100 px-3 py-1 rounded-md">
            {/* Re */}
            NEGATIVO
          </span>
        </div>

        {/* Filtro de Tipo de Feedback */}
        <Filters/>

        {/* Comentários */}
        <div className="border">
          <h3 className="text-lg font-semibold text-gray-700 my-4 ml-4">Comentários</h3>
          <hr/>
          <div className="space-y-2 max-h-[400px] overflow-y-auto px-4 py-2">
            {Array.from({ length: 40 }).map((_, index) => (
              <div
                key={index}
                className="border-b border-gray-200 pb-2"
              >
                <p className="text-gray-600">O sanduíche era muito gostoso.</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default FeedbackComponent;
