import React from "react";
import Graph from "../../components/Grafico";
import Filters from "../../components/Filtro";
import Table from "../../components/Tabela";

const FeedbackComponent = () => {
  return (
    <div className="flex flex-col lg:flex-row p-8 gap-8 bg-backgroundcolor min-h-screen">
      <Graph/>

      {/* Seção de Feedback */}
      <div className="flex-1 lg:max-w-lg bg-white shadow-md rounded-lg p-6">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-bold text-textcolor">Feedback Geral do Produto: </h2>
          <span className="text-sm font-bold text-red-600 bg-red-100 px-3 py-1 rounded-md">
            {/* Refatorar aqui para aparecer o maior feedback (seja negativo, positivo ou neutro) */}
            NEGATIVO
          </span>
        </div>

        <Filters/>

        {/* Comentários */}
        <Table/>
      </div>
    </div>
  );
};

export default FeedbackComponent;
